$(function(){$(window).scroll(function(){var scrollt=document.documentElement.scrollTop+document.body.scrollTop;if(scrollt>200){$("#back_top").fadeIn(400)}else{$("#back_top").stop().fadeOut(400)}if(scrollt>400){$('#catalog').css('position','fixed');$('#catalog').css('top','40px')}else{$('#catalog').css('position','absolute');$('#catalog').css('top','')}});$("#back_top").click(function(){$("html,body").animate({scrollTop:"0px"},200)})});